mapper.select:
select * from tbl_vehicle_maintenance

    delete_time is null

        and id = #{id}


        and vehicle_info_id = #{vehicleInfoId}


        and name = #{name}


        and maintenance_time = #{maintenanceTime}


        and handled_by = #{handledBy}


        and money_spent = #{moneySpent}

    order by create_time desc
